
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer not null primary key auto_increment, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "password_resets" ("email" varchar not null, "token" varchar not null, "created_at" datetime);
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer not null primary key autoincrement, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime default CURRENT_TIMESTAMP not null);
CREATE TABLE IF NOT EXISTS "tags" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "title" varchar not null);
CREATE TABLE IF NOT EXISTS "post" ("id" integer not null primary key autoincrement, "title" varchar, "post" text, "image" varchar, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, "slug" varchar, "status" integer not null default '0', "type" varchar default 'post', "post_id" integer, "tags_id" integer, "ip" varchar, foreign key("user_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "post_tags" ("id" integer not null primary key autoincrement, "tags_id" integer not null, "post_id" integer not null, foreign key("tags_id") references "tags"("id"), foreign key("post_id") references "post"("id"));
CREATE TABLE IF NOT EXISTS "users"
(
    id                INTEGER not null
        primary key autoincrement,
    name              varchar not null,
    email             varchar not null,
    email_verified_at datetime,
    password          varchar not null,
    adress            varchar,
    phone             varchar,
    presentation      TEXT,
    jobTitle          varchar,
    firstName         varchar,
    lastName          varchar,
    remember_token    varchar,
    created_at        datetime,
    updated_at        datetime,
    image             varchar
);
CREATE TABLE IF NOT EXISTS "roles" ("id" integer not null primary key autoincrement, "name" varchar not null, "description" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "role_users"
(
    id         INTEGER not null
        primary key autoincrement,
    users_id   INTEGER not null
        references users
            on update cascade on delete cascade,
    role_id    INTEGER not null
        references roles
            on update cascade on delete cascade,
    created_at datetime,
    updated_at datetime
);
CREATE TABLE IF NOT EXISTS "options_table"
(
    id           INTEGER not null
        primary key autoincrement,
    option_name  TEXT    not null,
    option_value TEXT,
    created_at   date,
    updated_at   date
, "type" varchar default 'default', "options_id" integer);
CREATE TABLE IF NOT EXISTS "contact" ("id" integer not null primary key autoincrement, "email" varchar not null, "text" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar, "ip" varchar);
CREATE TABLE IF NOT EXISTS "infos_tags" ("id" integer not null primary key autoincrement, "tags_id" integer not null, "infos_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("tags_id") references "tags"("id"), foreign key("infos_id") references "infos"("id"));
CREATE TABLE IF NOT EXISTS "infos"
(
    id          INTEGER not null
        primary key autoincrement,
    created_at  datetime,
    updated_at  datetime,
    title       varchar not null,
    description varchar,
    link        varchar,
    type        varchar,
    datestart   date,
    dateend     date,
    duree       INTEGER,
    image       varchar,
    users_id    INTEGER not null
);
CREATE TABLE IF NOT EXISTS "firewall_ips" ("id" integer not null primary key autoincrement, "ip" varchar not null, "log_id" integer, "blocked" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime, "deleted_at" datetime);
CREATE TABLE IF NOT EXISTS "firewall_logs" ("id" integer not null primary key autoincrement, "ip" varchar not null, "level" varchar not null default 'medium', "middleware" varchar not null, "user_id" integer, "url" text, "referrer" varchar, "request" text, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime);
CREATE TABLE IF NOT EXISTS "seo" ("id" integer not null primary key autoincrement, "model_type" varchar not null, "model_id" integer not null, "description" text, "title" varchar, "image" varchar, "author" varchar, "robots" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "comment" ("id" integer not null primary key autoincrement, "message" varchar not null, "post_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("post_id") references "post"("id"));
CREATE TABLE IF NOT EXISTS "menu" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer not null primary key autoincrement, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "image" ("id" integer not null primary key autoincrement, "name" varchar not null, "file" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
DELETE FROM sqlite_sequence;
CREATE INDEX "password_resets_email_index" on "password_resets" ("email");
CREATE UNIQUE INDEX users_email_unique
    on users (email);
CREATE UNIQUE INDEX "roles_name_unique" on "roles" ("name");
CREATE INDEX options_table_id_index
    on options_table (id);
CREATE INDEX "firewall_ips_ip_index" on "firewall_ips" ("ip");
CREATE INDEX "firewall_logs_ip_index" on "firewall_logs" ("ip");
CREATE INDEX "seo_model_type_model_id_index" on "seo" ("model_type", "model_id");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "image_name_unique" on "image" ("name");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
COMMIT;
